mapper.select:
select
id, user_phone, pass_word, registration_time, name, user_sex, user_address,user_status
from user_info



        and name like #{uname}



        and user_phone like #{uphone}



        and user_address like #{uaddress}


        and user_status = #{userInfo.status}


order by id

mapper.update:
update user_info
user_phone=#{phone}, pass_word=#{passWord}, registration_time=#{registrationTime}, name=#{name}, user_sex=#{sex}, user_address=#{address}, user_status=#{status}
id=#{id}
